import React, { Component } from "react";
import store from './store'
import Products from "./components/Products";
import Filter from "./components/Filter";
import Basket from "./components/Basket";
import { Provider } from 'react-redux'
import "./App.css";
import Footer from "./components/Footer";


class App extends Component {
  constructor() {
    super()

    this.state = {
       sort: '', size: '', products: [], filteredProducts: [], cartItems: [] };
    this.handleChangeSort = this.handleChangeSort.bind(this);
    this.handleChangeSize = this.handleChangeSize.bind(this);
    this.handleAddToCart = this.handleAddToCart.bind(this);
    this.handleRemoveFromCart = this.handleRemoveFromCart.bind(this);
  };
  componentDidMount(){
  //   fetch("http://localhost:8000/products").then(res => res.json())
  //   .then(data => this.setState({
  //     products: data,
  //     filteredProducts: data
  //   }));
    if(localStorage.getItem('cartItems')){
      this.setState({cartItems: JSON.parse(localStorage.getItem('cartItems'))});
    }
  }

  handleChangeSort(e){
    this.setState({sort: e.target.value});
    this.listProducts();
  }

  handleChangeSize(e){
    this.setState({size: e.target.value});
    this.listProducts();
  }

listProducts(){
  const {sort,size,products} = this.state;
  if(sort !== ''){
   products.sort((a,b) => (sort === 'lowestprice') ? (a.price > b.price ? 1 : -1) : 
    (a.price < b.price ? 1 : -1))
          } else {
             products.sort((a,b) => (a.id > b.id ? 1 : -1));
          }
          if(size !== ''){
            return {filteredProducts: products.filter(a => 
              a.availableSizes.indexOf(size.toUpperCase()) >= 0
            )}
          }
          this.setState({
            filteredProducts: products
          })
}

  handleAddToCart(e, product) {
    const cartItems = [...this.state.cartItems];
    let productAlreadyInCart = false;
    cartItems.forEach(item => {
      if(item.id === product.id){
        productAlreadyInCart = true;
        item.count++;
      }
    });
    if(!productAlreadyInCart){
      cartItems.push({...product, count:1})
    }
    this.setState({
         cartItems
    },()=>{
      localStorage.setItem("cartItems", JSON.stringify(cartItems));
    })
  }

  handleRemoveFromCart(e, item) {
    const cartItems = this.state.cartItems.filter( el => el.id !== item.id);
    this.setState({
      cartItems
    },()=>{
    localStorage.setItem('cartItems',cartItems)
  })
}

  render() {
    return (
      <Provider store={store}>
     <div className="container">
       <h1>Glamour-Bandixon - Erkaklar kiyim do'koni</h1>
       <hr/>
       <div className="row">
         <div className="col-md-8">
           <Filter size={this.state.size} sort={this.state.sort} handleChangeSize={this.handleChangeSize}
           handleChangeSort={this.handleChangeSort} count={this.state.filteredProducts.length} />
           <hr/>
          <Products products={this.state.filteredProducts} handleAddToCart={this.handleAddToCart} />
         </div>
         <div className="col-md-4">
            <Basket cartItems={this.state.cartItems} handleRemoveFromCart={this.handleRemoveFromCart} />
       </div>
       </div> 
     </div>
     <div>
         <Footer  />
       </div>
     </Provider>

    );

  }
}

export default App;
