import React, { Component } from "react";
import util from '../util'
import { connect } from 'react-redux'
// import { fetchProducts } from '../actions/productActions'
import { addToCart } from '../actions/cartActions'
class Products extends Component {
  constructor(props) {
    super(props)
  
    this.state = 
      {
        "products": [
          {
            "id": 1,
            "sku": 10000,
            "title": "Erkaklar uchun ko'ylak",
            "description": "",
            "availableSizes": ["XS", "L", "XL", "XXL"],
            "price": 85000
          },
          {
            "id": 2,
            "sku": 10001,
            "title": "JEUSS",
            "description": "",
            "availableSizes": ["M", "XXL"],
            "price": 165000
          },
          {
            "id": 3,
            "sku": 10002,
            "title": "Stefano Ricci Fatto in Italia",
            "description": "White DGK Script",
            "availableSizes": ["XXXL"],
            "price": 300000
          },
          {
            "id": 4,
            "sku": 10003,
            "title": "Stefano Ricci Fatto in Italia",
            "description": "Born On The Streets",
            "availableSizes": ["XL","XXXL"],
            "price": 180000
           },
      
          {
            "id": 5,
            "sku": 10004,
            "title": "BALLERS",
            "description": "Tso 3D Short Sleeve",
            "availableSizes": ["XS", "L"],
            "price": 100000 
          },
      
          {
            "id": 6,
            "sku": 10005,
            "title": "HUGO BOSS ORIGINAL",
            "description": "Man Tie Dye Cinza Grey",
            "availableSizes": ["XXS", "XS","S","M"],
            "price": 120000
          },
      
          {
            "id": 7,
            "sku": 10006,
            "title": "WELLALUX",
            "description": "1977 Infantil",
            "availableSizes": ["XXS","XS","S","M"],
            "style": "Preto com listras brancas",
            "price": 140000
          },
      
          {
            "id": 8,
            "sku": 10007,
            "title": "BONELLI",
            "description": "",
            "availableSizes": ["M","XXXL"],
            "style": "Azul escuro",
            "price": 200000  
           },
          {
            "id": 9,
            "sku": 10008,
            "title": "TILTON, TURKIYA",
            "description": "",
            "availableSizes": ["M", "XXL"],
            "price": 160000
          },
          {
            "id": 10,
            "sku": 10009,
            "title": "LION, PEKIN",
            "description": "",
            "availableSizes": ["M", "XXL"],
            "price": 140000
          },
          {
            "id": 11,
            "sku": 10010,
            "title": "SALAMANDER, Pekin Fabrikasi",
            "description": "",
            "availableSizes": ["L", "XL"],
            "price": 350000
          },
          {
            "id": 12,
            "sku": 10011,
            "title": "Adidas",
            "description": "",
            "availableSizes": ["L", "XL"],
            "price": 300000
          },
          {
            "id": 13,
            "sku": 10012,
            "title": "Adidas",
            "description": "",
            "availableSizes": ["L", "XL"],
            "price": 330000
          },
          {
            "id": 14,
            "sku": 10013,
            "title": "Adidas",
            "description": "",
            "availableSizes": ["M","L", "XL"],
            "price": 250000
          },
          {
            "id": 15,
            "sku": 10014,
            "title": "Pekin Fabrika",
            "description": "",
            "availableSizes": ["L", "XL"],
            "price": 350000
          }
    
        ]
      
    };
  };
  
  // componentDidMount(){
  //   this.props.fetchProducts();
  // }
  render() {
    //  const productItems = this.props.products.map((product) => (
    //   <div className="col-md-4" key={product.id}>
    //     <div className="container text-center m-4">
    //       <a 
    //         href={`#${product.id}`}
    //         // onClick={(e) => this.props.handleAddToCart(e, product)}
    //       >
    //         <img className="rounded" style={{width: 220, height: 300}} src={`/products/${product.sku}.jpg`} alt={product.title} />
    //         <p style={{fontFamily: "cursive", color: "white"}}>{product.title}</p>
    //       </a>
    //       <div>
    //       <b style={{color: "white"}}>{util.formatCurrency(product.price)}</b>
    //       <button
    //         className="btn btn-success"
    //         onClick={() => this.props.addToCart(this.props.cartItems, product)}
    //       >
    //         Savatchaga qo'shish
    //       </button>
    //       </div>
    //     </div>
    //   </div>
    // )
    // );
    const productItems = this.state.products.map((product) => (
      <div className="col-md-4" key={product.id}>
        <div className="container text-center m-4">
          <a 
            href={`#${product.id}`}
            // onClick={(e) => this.props.handleAddToCart(e, product)}
          >
            <img className="rounded" style={{width: 220, height: 300}} src={`${product.sku}.jpg`} alt={product.title} />
            <p style={{fontFamily: "cursive", color: "white"}}>{product.title}</p>
          </a>
          <div>
          <b style={{color: "white"}}>{util.formatCurrency(product.price)}</b>
          <button
            className="btn btn-success"
            onClick={() => this.props.addToCart(this.props.cartItems, product)}
          >
            Savatchaga qo'shish
          </button>
          </div>
        </div>
      </div>
    )
    );

    return (
    <div className="row">{productItems}</div>
    )
  }
}
const mapStateToProps = state => ({
  products: state.products.filteredItems,
  cartItems: state.cart.items
})

export default connect(mapStateToProps,{fetchProducts, addToCart})(Products)
